mapper.select:
select distinct review_like_review_seq, count(*) over(partition by review_like_review_seq) as review_like_click
from review_like
where review_like_store_id = #{review_like_review_seq, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM review_like
WHERE review_like_review_seq = #{review_like_review_seq, jdbcType=VARCHAR} 
and review_like_user_id = #{review_like_user_id, jdbcType=VARCHAR}
